function downloadYamlFile(){if(0<editorResult.getValue().trim().length){var r=new Blob([""+editorResult.getValue()],{type:"text/plain;charset=utf-8"});"validate"!=converted?fileDownloadCB(r,"codeconvert.yaml"):openErrorDialog("Yaml is not converted to JSON / XML ")}else openErrorDialog("Sorry Result is Empty")}var spacing=" ";function getType(r){var n=typeof r;return r instanceof Array?"array":"string"==n?"string":"boolean"==n?"boolean":"number"==n?"number":"undefined"==n||null===r?"null":"hash"}function convert(r,n){switch(getType(r)){case"array":convertArray(r,n);break;case"hash":convertHash(r,n);break;case"string":convertString(r,n);break;case"null":n.push("null");break;case"number":n.push(r.toString());break;case"boolean":n.push(r?"true":"false")}}function convertArray(r,n){for(var e=0;e<r.length;e++){var t=[];convert(r[e],t);for(var a=0;a<t.length;a++)n.push((0==a?"- \n   ":spacing)+t[a])}}function convertHash(r,n){for(var e in r){var t=[];if(r.hasOwnProperty(e)){var a=r[e];convert(a,t);var o=getType(a);if("string"==o||"null"==o||"number"==o||"boolean"==o)n.push(normalizeString(e)+": "+t[0]);else{n.push(normalizeString(e)+": ");for(var i=0;i<t.length;i++)n.push(spacing+t[i])}}}}function normalizeString(r){return r.match(/^[\w]+$/)?r:JSON.stringify(r)}function convertString(r,n){n.push(normalizeString(r))}function json2yaml(r){"string"==typeof r&&(r=JSON.parse(r));var n=[];return convert(r,n),n.join("\n")}
